export function bfs(grid, startNode) {
    let queue = [];
    let visitedNodes = [];
    startNode.isVisited = true;
    visitedNodes.push(startNode);
    queue.push(startNode);
    while(queue.length !== 0) {
        let node = queue.shift();
        let neighbors = getNeighbors(grid, node);
        for(let i = 0; i < neighbors.length; i++) {
            if(neighbors[i].isWall) continue;
            if(neighbors[i].isFinish) return 
            neighbors[i].isVisited = true;
            visitedNodes.push(neighbors[i]);
            neighbors[i].distance += 1;
            neighbors[i].previousNode = node;
            queue.push(neighbors[i]);
        }

    }
}

function getNeighbors(node, grid) {
    const neighbors = [];
    const {col, row} = node;
    if(row > 0) neighbors.push(grid[row-1][col]);
    if(row < grid.length - 1) neighbors.push(grid[row+1][col]);
    if(col > 0) neighbors.push(grid[row][col-1]);
    if(col < grid[0].length - 1) neighbors.push(grid[row][col+1]);
    return neighbors.filter(neighbor => !neighbor.isVisited);
}
